import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Dashboard.css';

const PartnerDashboard = () => {
    const [leads, setLeads] = useState([]);
    const [newLead, setNewLead] = useState({
        name: '',
        email: '',
        phone_number: '',
        company: '',
        description: '',
    });

    useEffect(() => {
        const fetchLeads = async () => {
            const response = await axios.get('/api/leads/', {
                headers: { Authorization: `Token ${localStorage.getItem('token')}` },
            });
            setLeads(response.data);
        };
        fetchLeads();
    }, []);

    const handleInputChange = (e) => {
        setNewLead({ ...newLead, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.post('/api/leads/', newLead, {
            headers: { Authorization: `Token ${localStorage.getItem('token')}` },
        });
        alert('Lead submitted successfully!');
    };

    return (
        <div className="dashboard">
            <h1>Partner Dashboard</h1>
            <form className="lead-form" onSubmit={handleSubmit}>
                <input name="name" placeholder="Name" onChange={handleInputChange} required />
                <input name="email" placeholder="Email" type="email" onChange={handleInputChange} required />
                <input name="phone_number" placeholder="Phone Number" onChange={handleInputChange} required />
                <input name="company" placeholder="Company" onChange={handleInputChange} required />
                <textarea name="description" placeholder="Lead Details" onChange={handleInputChange} required />
                <button type="submit">Submit</button>
            </form>
            <h2>Your Leads</h2>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Company</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {leads.map((lead) => (
                        <tr key={lead.id}>
                            <td>{lead.name}</td>
                            <td>{lead.email}</td>
                            <td>{lead.phone_number}</td>
                            <td>{lead.company}</td>
                            <td>{lead.status}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default PartnerDashboard;
