import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Add useHistory for redirection

const LoginForm = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory(); // Initialize useHistory

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/api/login/', { username, password });
            localStorage.setItem('token', response.data.token);

            // Redirect based on the role
            if (response.data.role === 'admin') {
                history.push('/admin-dashboard'); // Redirect to admin dashboard
            } else if (response.data.role === 'partner') {
                history.push('/partner-dashboard'); // Redirect to partner dashboard
            }
        } catch (error) {
            alert('Invalid credentials');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                value={username} 
                onChange={(e) => setUsername(e.target.value)} 
                placeholder="Username" 
                required 
            />
            <input 
                type="password" 
                value={password} 
                onChange={(e) => setPassword(e.target.value)} 
                placeholder="Password" 
                required 
            />
            <button type="submit">Login</button>
        </form>
    );
};

export default LoginForm;
