import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Assuming React Router is used
import './Dashboard.css';

const AdminDashboard = () => {
    const [leads, setLeads] = useState([]);
    const [partner, setPartner] = useState({ name: '', email: '', password: '', phone: '' });
    const [showUserSection, setShowUserSection] = useState(false); // State for showing user management
    const navigate = useNavigate(); // Navigation for redirecting

    const apiBaseUrl = 'http://localhost:8000/api';

    const fetchLeads = async () => {
        try {
            const response = await axios.get(`${apiBaseUrl}/leads/`, {
                headers: { Authorization: `Token ${localStorage.getItem('token')}` },
            });
            setLeads(response.data);
        } catch (error) {
            console.error('Error fetching leads:', error);
        }
    };

    useEffect(() => {
        fetchLeads();
    }, []);

    const handleCreatePartner = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(`${apiBaseUrl}/create-partner/`, partner, {
                headers: { Authorization: `Token ${localStorage.getItem('token')}` },
            });
            if (response.status === 201) {
                alert('Partner created successfully!');
                setPartner({ name: '', email: '', password: '', phone: '' });
                navigate('/partner-dashboard'); // Redirect to partner dashboard
            }
        } catch (error) {
            console.error('Error creating partner:', error);
            alert('Error creating partner');
        }
    };

    const handlePartnerInputChange = (e) => {
        setPartner({ ...partner, [e.target.name]: e.target.value });
    };

    return (
        <div className="dashboard">
            <h1>Admin Dashboard</h1>
            {/* User Management Button */}
            <button onClick={() => setShowUserSection(!showUserSection)} className="btn btn-primary">
                {showUserSection ? 'Hide User Management' : 'User'}
            </button>

            {showUserSection && (
                <div className="user-management">
                    <h2>Create Partner</h2>
                    <form className="partner-form" onSubmit={handleCreatePartner}>
                        <input name="name" placeholder="Name" onChange={handlePartnerInputChange} value={partner.name} required />
                        <input name="email" placeholder="Email" type="email" onChange={handlePartnerInputChange} value={partner.email} required />
                        <input name="password" placeholder="Password" type="password" onChange={handlePartnerInputChange} value={partner.password} required />
                        <input name="phone" placeholder="Phone" onChange={handlePartnerInputChange} value={partner.phone} required />
                        <button type="submit" className="btn btn-success">Create Partner</button>
                    </form>
                </div>
            )}

            {/* Leads table omitted for brevity */}
        </div>
    );
};

export default AdminDashboard;
