import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Dashboard.css';
import { useNavigate } from 'react-router-dom';  // Import useNavigate

const AdminDashboard = () => {
    const [leads, setLeads] = useState([]);
    const [filteredLeads, setFilteredLeads] = useState([]);
    const [newLead, setNewLead] = useState({
        name: '',
        email: '',
        phone_number: '',
        company: '',
        description: '',
    });
    const [partner, setPartner] = useState({
        name: '',
        email: '',
        password: '',
        phone: '',
    });
    const [searchTerm, setSearchTerm] = useState('');
    const [filterStatus, setFilterStatus] = useState('All');
    const [showLeadForm, setShowLeadForm] = useState(false); // To toggle the lead form visibility
    const apiBaseUrl = 'http://localhost:8000/api';
    const navigate = useNavigate();  // Initialize navigate

    // Fetch leads
    const fetchLeads = async () => {
        try {
            const response = await axios.get(`${apiBaseUrl}/leads/`, {
                headers: { Authorization: `Token ${localStorage.getItem('token')}` },
            });
            setLeads(response.data);
            setFilteredLeads(response.data);
        } catch (error) {
            console.error('Error fetching leads:', error);
        }
    };

    useEffect(() => {
        fetchLeads();
    }, []);

    const handleInputChange = (e) => {
        setNewLead({ ...newLead, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(`${apiBaseUrl}/leads/`, newLead, {
                headers: { Authorization: `Token ${localStorage.getItem('token')}` },
            });
            if (response.status === 201) {
                alert('Lead submitted successfully!');
                setNewLead({ name: '', email: '', phone_number: '', company: '', description: '' });
                fetchLeads();
                setShowLeadForm(false);  // Hide form after submission
            }
        } catch (error) {
            console.error('Error submitting lead:', error);
            alert('Error submitting lead');
        }
    };

    const handleUpdateStatus = async (leadId, status) => {
        try {
            await axios.post(`${apiBaseUrl}/leads/${leadId}/update-status/`, { status }, {
                headers: { Authorization: `Token ${localStorage.getItem('token')}` },
            });
            setLeads((prevLeads) =>
                prevLeads.map((lead) => (lead.id === leadId ? { ...lead, status } : lead))
            );
        } catch (error) {
            console.error('Error updating lead status:', error);
        }
    };

    const handlePartnerInputChange = (e) => {
        setPartner({ ...partner, [e.target.name]: e.target.value });
    };

    const handleCreatePartner = async (e) => {
        e.preventDefault();
        navigate('/partner-dashboard');
    
        try {
            const response = await axios.post(`${apiBaseUrl}/create-partner/`, partner, {
                headers: { Authorization: `Token ${localStorage.getItem('token')}` },
            });
            console.log(response); // Log the response for debugging
            alert('Partner created successfully!');
            setPartner({ name: '', email: '', password: '', phone: '' });
        } catch (error) {
            console.error('Error creating partner:', error);
            alert('Error creating partner');
        }
    };

    const handleSearch = (e) => {
        setSearchTerm(e.target.value);
        filterLeads(e.target.value, filterStatus);
    };

    const handleFilter = (e) => {
        setFilterStatus(e.target.value);
        filterLeads(searchTerm, e.target.value);
    };

    const filterLeads = (search, status) => {
        let filtered = leads;
        if (search) {
            filtered = filtered.filter((lead) =>
                lead.name.toLowerCase().includes(search.toLowerCase()) ||
                lead.email.toLowerCase().includes(search.toLowerCase())
            );
        }
        if (status !== 'All') {
            filtered = filtered.filter((lead) => lead.status === status);
        }
        setFilteredLeads(filtered);
    };

    return (
        <div className="dashboard">
            <h1>Admin Dashboard</h1>

            {/* Button to toggle Add Lead Form */}
            <button onClick={() => setShowLeadForm(!showLeadForm)} className="btn btn-primary mb-4">
                {showLeadForm ? 'Cancel' : 'Add Lead'}
            </button>

            {/* Show Lead Form only when showLeadForm is true */}
            {showLeadForm && (
                <div>
                    <h2>Submit Lead</h2>
                    <form className="lead-form" onSubmit={handleSubmit}>
                        <input name="name" placeholder="Name" onChange={handleInputChange} value={newLead.name} required />
                        <input name="email" placeholder="Email" type="email" onChange={handleInputChange} value={newLead.email} required />
                        <input name="phone_number" placeholder="Phone Number" onChange={handleInputChange} value={newLead.phone_number} required />
                        <input name="company" placeholder="Company" onChange={handleInputChange} value={newLead.company} required />
                        <textarea name="description" placeholder="Lead Details" onChange={handleInputChange} value={newLead.description} required />
                        <button type="submit">Submit Lead</button>
                    </form>
                </div>
            )}

            {/* Search and Filter Section */}
            <div className="search-filter">
                <input type="text" placeholder="Search Leads" value={searchTerm} onChange={handleSearch} className="form-control" />
                <select value={filterStatus} onChange={handleFilter} className="form-select">
                    <option value="All">All Statuses</option>
                    <option value="Accepted">Accepted</option>
                    <option value="Rejected">Rejected</option>
                    <option value="Pending">Pending</option>
                </select>
            </div>

            {/* Leads Table */}
            <h2>Leads Overview</h2>
            <table className="table table-striped table-bordered">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Company</th>
                        <th>Status</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredLeads.map((lead) => (
                        <tr key={lead.id}>
                            <td>{lead.name}</td>
                            <td>{lead.email}</td>
                            <td>{lead.phone_number}</td>
                            <td>{lead.company}</td>
                            <td>{lead.status}</td>
                            <td>
                                <button onClick={() => handleUpdateStatus(lead.id, 'Accepted')} className="btn btn-success btn-sm">Accept</button>
                                <button onClick={() => handleUpdateStatus(lead.id, 'Rejected')} className="btn btn-danger btn-sm">Reject</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminDashboard;
