import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './LoginForm.css';

const LoginForm = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');  // State for error messages
    const navigate = useNavigate();

    const handleLoginSubmit = async (e) => {
        e.preventDefault();
        setErrorMessage('');  // Reset error message on new attempt
        try {
            const response = await axios.post('http://localhost:8000/api/login/', { username, password });
    
            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
                // Navigate based on the role (admin or partner)
                if (response.data.role === 'admin') {
                    navigate('/admin-dashboard');
                } else if (response.data.role === 'partner') {
                    navigate('/partner-dashboard');
                }
            } else {
                setErrorMessage('Invalid credentials'); // Show error if no token received
            }
        } catch (error) {
            console.error('Login failed:', error);
            // Handle 404 or other errors gracefully
            if (error.response && error.response.status === 404) {
                setErrorMessage('API endpoint not found. Please check the server.');
            } else {
                setErrorMessage('An error occurred. Please try again later.');
            }
        }
    };

    return (
        <div className="d-flex justify-content-center align-items-center vh-100 bg-gradient">
            <div className="card shadow-lg p-5 custom-card">
                <h2 className="text-center mb-4 custom-title">Login</h2>
                <form onSubmit={handleLoginSubmit}>
                    <div className="mb-3">
                        <label htmlFor="username" className="form-label">Username</label>
                        <input
                            type="text"
                            id="username"
                            className="form-control custom-input"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            placeholder="Enter your username"
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="password" className="form-label">Password</label>
                        <input
                            type="password"
                            id="password"
                            className="form-control custom-input"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Enter your password"
                            required
                        />
                    </div>
                    <button type="submit" className="btn btn-lg btn-primary w-100 custom-btn">Login</button>
                </form>
                {errorMessage && (
                    <div className="alert alert-danger mt-3">
                        {errorMessage}
                    </div>
                )}
            </div>
        </div>
    );
};

export default LoginForm;
